function countDivElementsWithBorder(e){return document.createElement("div").innerHTML=e,document.querySelectorAll("div.border").length}function countDivElementsWithBorderInHTMLFile(t){return fetch("http://localhost3000/src/index/"+t).then(e=>{if(e.ok)return e.text();throw new Error("Failed to load HTML file: "+t)}).then(e=>countDivElementsWithBorder(e))}function countTotalDivElementsWithBorder(e){e=e.map(e=>countDivElementsWithBorderInHTMLFile(e));return Promise.all(e).then(e=>e.reduce((e,t)=>e+t,0))}fetch("http://localhost:3000/src/index/list_of_html_files.txt").then(e=>{if(e.ok)return e.text();throw new Error("Failed to load the list of HTML files")}).then(e=>{e=e.split("\n").filter(Boolean);return console.log(e),countTotalDivElementsWithBorder(e)}).then(e=>{console.log('Total div elements with class "border": '+e)}).catch(e=>{console.error("Error: "+e.message)});